mapper.select:
select id, agency_name as agencyName, description as description, sys_user_name as sysUserName, sys_user_password as sysUserPassword, mobilephone as mobilephone, email as email, open_id as openId, is_cooperation as isCooperation, province, city, area, address, wechat_image_path as wechatImagePath, logo_image_path as logoImagePath, create_time as createTime, update_time as updateTime, latitude as latitude, longitude as longitude, status as status, type as type, is_del as isDel from agency_base where is_del = 0
and city = #{city}
and province = #{province}
and area = #{area}
and mobilephone = #{mobilephone}
and address like '%${address}%'
and status = #{status}
and agency_name like '%${agencyName}%'
and city like '%${city}%'
and create_time > #{searchStartTime}
and create_time < #{searchEndTime}
order by create_time desc limit #{start},#{length}

select id, agency_name as agencyName, logo_image_path as logoImagePath 
from agency_base 
where agency_name like #{name} 

	and city = #{city}


	and province = #{province}


	and area = #{area}

limit #{offset}, #{rows}

select count(id) 
from agency_base
where is_del = 0

	and mobilephone = #{mobilephone}


	and address like '%${address}%'


	and create_time > #{searchStartTime}


	and create_time < #{searchEndTime}
