mapper.select:
select id, open_id as openId, union_id as unionId, favorite_id as favoriteId, type, status, 
	create_time as createTime, update_time as updateTime,is_del as isDel
from favorite 
where is_del = 0

	and open_id = #{openId}


	and union_id = #{unionId}


	and favorite_id = #{favoriteId}


	and type = #{type}

order by create_time
limit 0,1

mapper.update:
update favorite


		open_id = #{openId},


		union_id = #{unionId},


		favorite_id = #{favoriteId},


		type = #{type},


		status = #{status},

	update_time = now()
	where 1 = 1

		and open_id = #{openId}


		and union_id = #{unionId}


		and favorite_id = #{favoriteId}


		and type = #{type}


		and id = #{id}

delete from favorite
where is_del = 0

	and open_id = #{openId}


	and union_id = #{unionId}


	and favorite_id = #{favoriteId}


	and type = #{type}

mapper.select:
select id, open_id as openId, union_id as unionId, favorite_id as favoriteId, type, status, 
	create_time as createTime, update_time as updateTime,is_del as isDel
from favorite 
where type = #{type}
and open_id = #{openId}
