mapper.select:
select p1.id, p1.category, p1.description, p1.images, p1.contact_name, p1.mobilephone, p1.city, p1.location, p1.update_time
from publish_content p1 right join parameter p2 on p1.category = p2.para_value
where 1=1

	and p1.city = #{city}


	and p1.province = #{province}


	and p1.area = #{area}

and p2.para_domain = 'publish.category'
and p2.para_value = #{paraId}
limit #{offset}, #{rows}
